<script>
    function update(obj)
    {
        if ($(obj).attr('checked'))
        {
            $data = $(obj).val().split(',');
            $desc = $(obj).data('desc');
            $('#total_money').html($data[1]);
            $('#desc').html($desc);
            console.log($data[1]);
            console.log($desc);
        }
    }

    $('.goods').change(function() {
        console.log('toggle');
        console.log(this);
        $(this).attr('checked', true);
        update(this);
    });

    function init() {
        $items = $('.goods');
        for($i=0; $i<$items.length; $i++) {
            update($items[$i]);
        }
    };

    init();
</script>
